	subroutine copy44(from,to,n)CC*******************************************************************************CC       Copyright (C) 1993, California Institute of Technology.  U.S.C       Government Sponsorhip under NASA Contract NAS7-918 isC       acknowledged.CC*******************************************************************************CC	copies a real*4 arrayC	real*4 from(N)        real*4 to(N)	do i=1,n	  to(i)=from(i)	end do	return	end