	subroutine newpage ( ipage, ISKIP )	integer ipage, ISKIP        character adate*9, atime*8	call date ( adate )        call time ( atime )        write (8,3025) adate, atime, ipage 3025   format ('1',/,' ',77('*'),/,     &      ' * ', A9,1x,A8,T35, 'GTARG Report',     &          T69,'Page ',i3,' *',/,     &       ' ',77('*') )	IPAGE = IPAGE+1	IF (ISKIP.GT.0) THEN 	   DO I=1,ISKIP	      WRITE(8,*) ' '   	   END DO	END IF	return	end