	subroutine shift4( X, SHIFT ,N)CC*******************************************************************************CC       Copyright (C) 1993, California Institute of Technology.  U.S.C       Government Sponsorhip under NASA Contract NAS7-918 isC       acknowledged.CC*******************************************************************************CC	adds a constant SHFIT to first N elements of array XC        real*4 X(N), SHIFT	do i=1,N	  X(i) = X(I) + SHIFT	end do	return	end